var link=document.querySelector(".buttons-contacts"),popup=document.querySelector(".popup"),close=document.querySelector(".popup-close");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("popup-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("popup-show")}),window.addEventListener("keydown",function(a){27==a.keyCode&&popup.classList.contains("popup-show")&&popup.classList.remove("popup-show")});
